import SimplexNoise from 'simplex-noise';
import * as THREE from 'three'
import { generateTexture, map } from '../Helpers/libs/TerrainBuilder';

export default function Terrain4( scene )
{
    console.log( generateTexture( ), 'd' )
    const terrain = generateMeshFromTexture( generateTexture( ) )
    scene.add( terrain )
    //  scene.add()

    function getVertices( geometry )
    {
        if ( geometry.isBufferGeometry )
        {
            const positions = geometry.attributes.position
            const vector = new THREE.Vector3( );
            for ( let i = 0; i < positions.count; i++ )
            {
                vector.fromBufferAttribute( positions, i );
                console.log( vector )
            }
        }
    }

    function generateMeshFromTexture( data )
    {
        //make plane geometry
        const geo = new THREE.PlaneBufferGeometry( data.width, data.height, data.width, data.height + 1 )
        console.log( geo.attributes.position.array )
        console.log( geo )
        for ( let j = 0; j < data.height; j++ )
        {
            for ( let i = 0; i < data.width; i++ )
            {
                const n = ( j * ( data.width ) + i )
                const nn = ( j * ( data.width + 1 ) + i )
                const col = data.data[ n * 4 ]
                const v1 = geo.attributes.position.array[ nn ]
                // v1.z = map( col, 0, 255, -10, 10 )
                // if ( v1.z > 2.5 ) v1.z *= 1.3
            }
        }
        geo.faces.forEach( f =>
        {
            const a = geo.vertices[ f.a ]
            const b = geo.vertices[ f.b ]
            const c = geo.vertices[ f.c ]
            const avg = ( a.z + b.z + c.z ) / 3
            const max = Math.max( a.z, Math.max( b.z, c.z ) )

            //if average is below water, set to 0
            if ( avg < 0 )
            {
                a.z = 0
                b.z = 0
                c.z = 0
            }
            //assign colors based on the highest point of the face
            if ( max <= 0 ) return f.color.set( COLORS.WATER )
            if ( max <= 1.5 ) return f.color.set( COLORS.GRASS )
            if ( max <= 3.5 ) return f.color.set( 0xeecc44 )
            if ( max <= 5 ) return f.color.set( 0xcccccc )
            f.color.set( 'white' )
        } )
        geo.colorsNeedUpdate = true
        geo.verticesNeedUpdate = true
        geo.computeFlatVertexNormals( )
        const mesh = new THREE.Mesh( geo, new THREE.MeshLambertMaterial(
        {
            // color:'white',
            // wireframe:true,
            vertexColors: THREE.VertexColors,
            flatShading: true,
        } ) )
        return mesh
    }

    this.update = function( )
    {
        return null
    }
}
